import React, { Component, useRef } from 'react'
// import styles from '../styles/components/caroussel.css'
import 'react-slideshow-image/dist/styles.css'
import { Slide } from 'react-slideshow-image';
import PrevBtnSlider from './prevBtnSlider.tsx';
import NextBtnSlider from './nextBtnSlider.tsx';

const properties = {
	duration: 5000,
	autoplay: false,
	transitionDuration: 500,
	arrows: true,
	infinite: true,
	easing: "ease",
	indicators: (i) => <div className="indicator">{i + 1}</div>
};
const slideImages = [
  'slider/2.jpg',
  'slider/1.jpg',
];

interface Props {
    // name:string;
    // optionalParam?:number;
  //empty in our case
}

interface State {
  //empty in our case
}

export default class Test extends React.Component<Props, State> {
	constructor(props) {
		super(props);
		this.state = {

		};
	}

	displaySlide = () => {
		const slideRef = useRef(null);
		return (
		<div className="slide-container">
			<Slide ref={slideRef} {...properties}>
				{slideImages.map((each, index) => (
					<div key={index} className="each-slide">
						<img src={each} alt="sample" />
					</div>
				))}
			</Slide>
			<PrevBtnSlider action={() => slideRef.goBack()}/>
			<NextBtnSlider/>
		</div>
		)

	}

	render() {
		return (
			<div className="slide-wrapper-container">
				{this.displaySlide()}
			</div>
		);
	}
}
