import React, { Component } from 'react';
import Icon from "@mdi/react";
import { mdiCloseCircle } from "@mdi/js";


export default class Pop extends Component {
	constructor(props){
		// console.log("hidden props", props.hidden);
		super(props)
		this.state = {
			hidden: (typeof props.hidden !== "undefined") ? props.hidden : true,
			content: (typeof props.content !== "undefined") ? props.content : () => {return(<p>Nothing to display</p>)},
			onQuit: (typeof props.onQuit !== "undefined") ? props.onQuit : () => {return},
		}
	}

	off = () => {
		this.state.onQuit();
		this.setState({hidden:true});
	}

	render() {
		if (this.state.hidden) {
			return null;
		}
		return (
			<div className={"pop_pad"}>
				<div className={"pop_main"}>
					<div className={"pop"}>
						<div className={"pop_off"} onClick={this.off}>
							<div className={"pop_off_pad"}>
								<Icon path={mdiCloseCircle} size={4} color="#eaeaea" />
							</div>
						</div>
						{typeof this.state.content === 'function' ?
							this.state.content()
						:
							this.state.content
						}
					</div>
				</div>
			</div>
		)
	}
}
