import React, { Component, useRef } from 'react'
// import styles from '../styles/components/caroussel.css'
import 'react-slideshow-image/dist/styles.css'
import { Slide } from 'react-slideshow-image';
import PrevBtnSlider from './prevBtnSlider.tsx';
import NextBtnSlider from './nextBtnSlider.tsx';


const test = () => {
	const slideRef = useRef();
	const properties = {
		duration: 5000,
		autoplay: false,
		transitionDuration: 500,
		arrows: true,
		infinite: true,
		easing: "ease",
		indicators: (i) => <div className="indicator">{i + 1}</div>
	};
	const slideImages = [
	  'slider/2.jpg',
	  'slider/1.jpg',
	];
	const back = () => {
		slideRef.current.goBack();
	}

	const next = () => {
		slideRef.current.goNext();
	}


  const goto = ({ target }) => {
    slideRef.current.goTo(parseInt(target.value, 10));
  }
	return (
		<div className="slide-wrapper-container">
			<div className="slide-container">
				<PrevBtnSlider action={back}/>
				<NextBtnSlider action={next}/>
				<Slide ref={slideRef} {...properties}>
					{slideImages.map((each, index) => (
						<div key={index} className="each-slide">
							<img src={each} alt="sample" />
						</div>
					))}
				</Slide>
			</div>
			 <div className="autoplay-buttons">
        <button type="button" onClick={back}>Back</button>
        <button type="button" onClick={next}>Next</button>
        <select onChange={goto}>
          <option>--Select--</option>
          <option value="0">First</option>
          <option value="1">Second</option>
          <option value="2">Third</option>
        </select>
      </div>
		</div>
	);
}
export default test;
