import React, { Component } from 'react'

import Icon from "@mdi/react";
import { mdiFullscreen, mdiFullscreenExit } from "@mdi/js";

interface Props {
    action:any;
    fullScreen:Boolean;
    // optionalParam?:number;
}

interface State {
    fullScreen:Boolean;
}

export default class FullScreenBtnSlider extends React.Component<Props, State> {
	constructor(props) {
		super(props)
		this.state = {
			fullScreen:this.props.fullScreen,
		}
	}

	render() {
		return (
			<div className={this.state.fullScreen ? "slider-full-screen-btn-container-full-screen" : "slider-full-screen-btn-container"} onClick={this.props.action}>
				{this.state.fullScreen?
					<Icon path={mdiFullscreenExit} title={"Plein écran"} size={3} color="white"/>
				:
					<Icon path={mdiFullscreen} title={"Plein écran"} size={3} color="white"/>
				}
			</div>
		);
	}
}
